INSERT INTO tools (slug, name, description, icon, color, system_prompt, output_schema) VALUES ('cv-fixer', 'CV Fixer', 'Modernize your resume for top-tier tech roles.', 'FileText', 'from-pink-500 to-rose-500', $$You are a savage FAANG recruiter. You hate fluff. Analyze the user's CV text. Return a strict JSON response. LAYOUT INSTRUCTIONS: Return a '_layout' field: '[[score]] [[summary]] [[fix_list]]'.$$, $$ { "type": "object", "properties": { "score": {"type": "integer", "unit": "/ 100"}, "summary": {"type": "string"}, "fix_list": {"type": "array", "items": {"type": "string"}}, "_layout": {"type": "string"} } } $$);
